import connectAdvanced from '../components/connectAdvanced'
import defaultSelectorFactory from './selectorFactory'

//the 'connect' function export is actually an HOF that return an HOC
//notice that the connectHOC is actually generated by another HOF call connectAdvanced
export default function connectHOF(
    //its input are two mapping functions
    mapStateToProps,
    mapDispatchToProps
  ) {
    //call connectAdvanced to generated the HOC based on the mapping functions
    const connectHOC = connectAdvanced(defaultSelectorFactory, {
      mapStateToProps,
      mapDispatchToProps,
    })
    return connectHOC;
}
